import {
    VStack,
    ButtonGroup,
    FormControl,
    Button,
    FormErrorMessage,
    FormLabel,
    Input, Heading
} from "@chakra-ui/react";
import {Form, Formik} from "formik";
import * as Yup from "yup"

export const Login = () => {

    return (
        <>
            <Formik
                initialValues={{username: "", password: ""}}
                validationSchema={Yup.object({
                    username: Yup.string()
                        .required("Username required!")
                        .min(6, "Username too short!")
                        .max(28, "Username too long!"),
                    password: Yup.string()
                        .required("Password required!")
                        .min(6, "Password too short!")
                        .max(28, "Password too long!"),
                })}
                onSubmit={(values, actions) => {
                    alert(JSON.stringify(values, null, 2));
                    console.log('info: %s', JSON.stringify(values, null, 2))
                    actions.resetForm();
                }}
            >
                <VStack
                    as={Form}
                    w={{base: "90%", md: "500px"}}
                    m={"auto"}
                    justify={"center"}
                    h={"100vh"}
                    spacing={"1rem"}
                >
                    <Heading>
                        Log In
                    </Heading>
                    <FormControl>
                        <FormLabel fontSize={"lg"}>Username</FormLabel>
                        <Input
                            name={"username"}
                            placeholdeer={"Enter username"}
                            autoComplete={"off"}
                            size={"lg"}
                        />
                        <FormErrorMessage>Invalid username</FormErrorMessage>
                    </FormControl>

                    <FormControl>
                        <FormLabel fontSize={"lg"}>Password</FormLabel>
                        <Input
                            type={"password"}
                            name={"password"}
                            placeholdeer={"Enter password"}
                            size={"lg"}
                            autoComplete={"off"}
                        />
                        <FormErrorMessage>Invalid password</FormErrorMessage>
                    </FormControl>

                    <ButtonGroup pt={"1rem"}>
                        <Button colorScheme={"teal"} type={"submit"}>Log in</Button>
                        <Button type={"submit"}>Create Account</Button>
                    </ButtonGroup>
                </VStack>
            </Formik>
        </>
    );
};
